DECLARE @StartDate date = '2021-01-01';
DECLARE @EndDate date = '2023-12-31';

WHILE @StartDate <= @EndDate
BEGIN
    INSERT INTO DimDate ([Date], Year, [Quarter], Month, Day, Weekday, Weekday_Number, Is_Weekend, Day_Of_Year, Date_String)
    VALUES (
        @StartDate,
        DATEPART(YEAR, @StartDate),
        DATEPART(QUARTER, @StartDate),
        DATEPART(MONTH, @StartDate),
        DATEPART(DAY, @StartDate),
        DATENAME(WEEKDAY, @StartDate),
        DATEPART(WEEKDAY, @StartDate),
        CASE WHEN DATEPART(WEEKDAY, @StartDate) IN (6, 7) THEN 1 ELSE 0 END,
        DATEPART(DAYOFYEAR, @StartDate),
        CONVERT(varchar(10), @StartDate, 120)
    );

    SET @StartDate = DATEADD(DAY, 1, @StartDate);
END

-- Verify the generated DimDate data
SELECT * FROM DimDate;
